do $$
declare
  ver_id uuid;
begin
  select id into ver_id from public.test_versions where step_id = 'oacp' and is_active and published_at <= now() order by published_at desc limit 1;
  if ver_id is null then raise exception 'No active test_versions row found for step oacp'; end if;
  delete from public.test_questions where version_id = ver_id;
end $$;
